import React from 'react';
import { Switch, Route, Link } from 'react-router-dom';
import './Cards.css';

const Card = ({ imgSrc, alt, label, title }) => {
  return (
    <div className="card content">
      <div className="card-content">
        <div className="card-img">
          <img src={imgSrc} alt={alt} />
        </div>
        <div className="card-label">{label}</div>
        <div className="card-title">{title}</div>
      </div>
    </div>
  );
};

const Cards = () => {
  return (
    <div className="cards">
      <Routes>
        <Route path="/" element={
          <Switch>
            <Route exact path="/">
              <Card
                imgSrc="https://images.unsplash.com/photo-1542751371-adc38448a05e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2850&q=80"
                alt="Gamer"
                label="E-Sports"
                title="Fnatic raises $19 million, shakes up leadership team"
              />
              <Card
                imgSrc="https://images.unsplash.com/photo-1547394765-185e1e68f34e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1950&q=80"
                alt="keyboard"
                label="Technology"
                title="Google Stadia: The Future of Gaming"
              />
              <Card
                imgSrc="https://images.unsplash.com/photo-1519326844852-704caea5679e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2034&q=80"
                alt="Controller"
                label="Consoles"
                title="PS5 won't launch before mid-2020"
              />
              <Link to="/signup" className="card form">
                <div className="form-title">Sign Up</div>
              </Link>
            </Route>
            <Route path="/signup">
              {/* Render the signup component */}
            </Route>
          </Switch>
        } />
      </Routes>
    </div>
  );
};

export default Cards;
